select distinct rSys.Name, arp.DisplayName, arp.Version, arp.Publisher, arp.InstallDate, os.Caption, wss.LastHardwareScan, lss.LastScanDate from  SMS_G_System_ADD_REMOVE_PROGRAMS_64 as arp left join SMS_R_System as rSys on rSys.ResourceId = arp.ResourceID left join SMS_G_System_WORKSTATION_STATUS as wss on wss.ResourceID = arp.ResourceID left join SMS_G_System_OPERATING_SYSTEM as os on os.ResourceID = arp.ResourceID left join SMS_G_System_LastSoftwareScan as lss on lss.ResourceID = arp.ResourceID where (LOWER(arp.DisplayName) like "%adobe%" or LOWER(arp.DisplayName) like "%macromedia%" or LOWER(arp.DisplayName) like "%acrobat%" or LOWER(arp.DisplayName) like "%creative suite%" or LOWER(arp.DisplayName) like "%after effects%" or LOWER(arp.DisplayName) like "%authorware%" or LOWER(arp.DisplayName) like "%captivate%" or LOWER(arp.DisplayName) like "%coldfusion%" or LOWER(arp.DisplayName) like "%contribute%" or LOWER(arp.DisplayName) like "%dreamweaver%" or LOWER(arp.DisplayName) like "%fireworks%" or LOWER(arp.DisplayName) like "%flex%builder%" or LOWER(arp.DisplayName) like "%framemaker%" or LOWER(arp.DisplayName) like "%freehand%" or LOWER(arp.DisplayName) like "%golive%" or LOWER(arp.DisplayName) like "%homesite%" or LOWER(arp.DisplayName) like "%illustrator%" or LOWER(arp.DisplayName) like "%incopy%" or LOWER(arp.DisplayName) like "%indesign%" or LOWER(arp.DisplayName) like "%pagemaker%" or LOWER(arp.DisplayName) like "%photoshop%" or LOWER(arp.DisplayName) like "%soundbooth%" or LOWER(arp.Publisher) like "%adobe%" or LOWER(arp.Publisher) like "%macromedia%") and LOWER(arp.DisplayName) not like "%microsoft%" order by rSys.Name