select  distinct rSys.Name0,arp.DisplayName00,arp.Version00,arp.Publisher00,arp.InstallDate00,os.Caption0,lss.LastUpdateDate,wss.LastHWScan from Add_Remove_Programs_64_DATA AS arp LEFT OUTER JOIN System_DISC AS rSys ON arp.MachineID = rSys.ItemKey  LEFT OUTER JOIN WorkstationStatus_DATA AS wss ON wss.MachineID = arp.MachineID  LEFT OUTER JOIN Operating_System_DATA AS os ON os.MachineID = arp.MachineID  LEFT OUTER JOIN SoftwareInventoryStatus AS lss ON lss.ClientId = arp.MachineID   where 
(LOWER(arp.DisplayName00) like '%adobe%' or LOWER(arp.DisplayName00) like '%macromedia%' or LOWER(arp.DisplayName00) like '%acrobat%' or LOWER(arp.DisplayName00) like '%creative suite%' or LOWER(arp.DisplayName00) like '%after effects%' or LOWER(arp.DisplayName00) like '%authorware%' or LOWER(arp.DisplayName00) like '%captivate%' or LOWER(arp.DisplayName00) like '%coldfusion%' or LOWER(arp.DisplayName00) like '%contribute%' or LOWER(arp.DisplayName00) like '%dreamweaver%' or LOWER(arp.DisplayName00) like '%fireworks%' or LOWER(arp.DisplayName00) like '%flex%builder%' or LOWER(arp.DisplayName00) like '%framemaker%' or LOWER(arp.DisplayName00) like '%freehand%' or LOWER(arp.DisplayName00) like '%golive%' or LOWER(arp.DisplayName00) like '%homesite%' or LOWER(arp.DisplayName00) like '%illustrator%' or LOWER(arp.DisplayName00) like '%incopy%' or LOWER(arp.DisplayName00) like '%indesign%' or LOWER(arp.DisplayName00) like '%pagemaker%' or LOWER(arp.DisplayName00) like '%photoshop%' or LOWER(arp.DisplayName00) like '%soundbooth%' or LOWER(arp.Publisher00) like '%adobe%' or LOWER(arp.Publisher00) like '%macromedia%') and LOWER(arp.DisplayName00) not like '%microsoft%' order by rSys.Name0