select distinct rSys.Name, os.Caption, rSys.ResourceDomainORWorkgroup, rSys.LastLogonUserName, lss.LastScanDate, wss.LastHardwareScan from  SMS_R_System as rSys full join SMS_G_System_OPERATING_SYSTEM as os on os.ResourceID = rSys.ResourceId full join SMS_G_System_LastSoftwareScan as lss on lss.ResourceID = rSys.ResourceId full join SMS_G_System_WORKSTATION_STATUS as wss on wss.ResourceID = rSys.ResourceId order by rSys.Name